<script>
export default {
    watch: {
        '$store.state.Title'(val) {
            this.$store.commit("title", "管理中心");
        }
    },
    mounted() {
        this.$store.commit("title", "管理中心");
    },
    created() {
    },
    methods: {
        DeleteCache() {
            this.$store.commit("load", true);
            this.$axios.post('/admin/cache', {
            }).then(res => {
                this.$store.commit("load", false);
                this.$store.commit("msg", res.data.message)
            }).catch(error => {
                console.log(error);
            });
            return;
        }
    }
}
</script>

